CREATE TABLE IF NOT EXISTS `PREFIX_index_box` (`id_box` int(11) NOT NULL AUTO_INCREMENT,`id_shop` int(11) NOT NULL,`image` varchar(255) NOT NULL,`bo_title` varchar(255) NOT NULL,`icon` varchar(45),`classes` varchar(255),`type` varchar(45) NOT NULL,`item_id` int(11) NOT NULL,`position` int(11) NOT NULL,`active` int(1) NOT NULL DEFAULT 0,PRIMARY KEY  (`id_box`)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS `PREFIX_index_box_lang` (`id_box` int(11) NOT NULL,`id_lang` int(11) NOT NULL,`title` varchar(255) NOT NULL,PRIMARY KEY  (`id_box`, `id_lang`)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;